mapper.update:
update phone_info
set album_id = #{albumId,jdbcType=INTEGER},
  phone_shumbnail_url = #{phoneShumbnailUrl,jdbcType=VARCHAR},
  phone_original_url = #{phoneOriginalUrl,jdbcType=VARCHAR},
  phone_praise_num = #{phonePraiseNum,jdbcType=INTEGER}
where phone_id = #{phoneId,jdbcType=INTEGER}

mapper.select:
select phone_id, album_id, phone_shumbnail_url, phone_original_url, phone_praise_num
from phone_info
where phone_id = #{phoneId,jdbcType=INTEGER}
