mapper.update:
update user_album_info
set self_album_id = #{selfAlbumId,jdbcType=VARCHAR},
  other_album_id = #{otherAlbumId,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select user_id, self_album_id, other_album_id
from user_album_info
where user_id = #{userId,jdbcType=INTEGER}
